;; gorilla-repl.fileformat = 1

;; **
;;; # Move a shape to the center of the scene
;;; 
;;; Say you have a shape inside a picture, and you want to move it somewhere else inside the same picture.
;;; 
;; **

;; @@
(ns unwavering-briars
 (:require
  [opencv3.utils :as u]
  [opencv3.colors.html :refer :all]
  [opencv3.core :refer :all]))
;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-nil'>nil</span>","value":"nil"}
;; <=

;; **
;;; The plan is defined as follow:
;;; 
;;; - load the picture, and add a quick border to get it to show properly here.
;;; - go into HSV color space to be able to select only the object we want use a color range
;;; - create a mask 
;; **

;; @@
(def img 
  (-> "resources/morph/cjy6M.jpg" 
      imread 
      (u/resize-by 0.4) 
      (copy-make-border! 1 1 1 1 
		BORDER_CONSTANT 
        (->scalar "#aabbcc"))))

(u/mat-view img)
;; @@
;; =>
;;; {"type":"html","content":"<img src=\"data:image/png;base64,\" width=\"256\" height=\"192\" alt=\"\" />","value":"#object[java.awt.image.BufferedImage 0x4a6624fd \"BufferedImage@4a6624fd: type = 5 ColorModel: #pixelBits = 24 numComponents = 3 color space = java.awt.color.ICC_ColorSpace@e1249da transparency = 1 has alpha = false isAlphaPre = false ByteInterleavedRaster: width = 256 height = 192 #numDataElements 3 dataOff[0] = 2\"]"}
;; <=

;; @@
(def hsv 
  (-> img clone (cvt-color! COLOR_BGR2HSV)))
(u/mat-view hsv)
;; @@
;; =>
;;; {"type":"html","content":"<img src=\"data:image/png;base64,\" width=\"256\" height=\"192\" alt=\"\" />","value":"#object[java.awt.image.BufferedImage 0x655203ab \"BufferedImage@655203ab: type = 5 ColorModel: #pixelBits = 24 numComponents = 3 color space = java.awt.color.ICC_ColorSpace@e1249da transparency = 1 has alpha = false isAlphaPre = false ByteInterleavedRaster: width = 256 height = 192 #numDataElements 3 dataOff[0] = 2\"]"}
;; <=

;; **
;;; ## thresh hold on yellow in hsv space
;; **

;; @@
(def mask-on-yellow (new-mat))
(in-range hsv (new-scalar 20 100 100) (new-scalar 40 255 255) mask-on-yellow)

(u/mat-view mask-on-yellow)
;; @@
;; =>
;;; {"type":"html","content":"<img src=\"data:image/png;base64,\" width=\"256\" height=\"192\" alt=\"\" />","value":"#object[java.awt.image.BufferedImage 0x1c319a33 \"BufferedImage@1c319a33: type = 10 ColorModel: #pixelBits = 8 numComponents = 1 color space = java.awt.color.ICC_ColorSpace@553b8b78 transparency = 1 has alpha = false isAlphaPre = false ByteInterleavedRaster: width = 256 height = 192 #numDataElements 1 dataOff[0] = 0\"]"}
;; <=

;; **
;;; ## find contours, and select first 1, (actually only one)
;; **

;; @@
(def contours (new-arraylist))
(find-contours mask-on-yellow contours (new-mat) RETR_EXTERNAL CHAIN_APPROX_SIMPLE)
(count contours)
;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-unkown'>1</span>","value":"1"}
;; <=

;; @@
(def background-color  (->scalar "#000000"))
; mask type CV_8UC1 is important !!
(def mask (new-mat (rows img) (cols img) CV_8UC1 background-color))

(u/mat-view mask)
;; @@
;; =>
;;; {"type":"html","content":"<img src=\"data:image/png;base64,\" width=\"256\" height=\"192\" alt=\"\" />","value":"#object[java.awt.image.BufferedImage 0x6d5f123 \"BufferedImage@6d5f123: type = 10 ColorModel: #pixelBits = 8 numComponents = 1 color space = java.awt.color.ICC_ColorSpace@553b8b78 transparency = 1 has alpha = false isAlphaPre = false ByteInterleavedRaster: width = 256 height = 192 #numDataElements 1 dataOff[0] = 0\"]"}
;; <=

;; **
;;; Here we draw the one contour we have found, using the *FILLED* setting of draw-contours. 
;; **

;; @@
(draw-contours mask contours 0 (new-scalar 255.0) FILLED)
(u/mat-view mask)
;; @@
;; =>
;;; {"type":"html","content":"<img src=\"data:image/png;base64,\" width=\"256\" height=\"192\" alt=\"\" />","value":"#object[java.awt.image.BufferedImage 0x4a827fbe \"BufferedImage@4a827fbe: type = 10 ColorModel: #pixelBits = 8 numComponents = 1 color space = java.awt.color.ICC_ColorSpace@553b8b78 transparency = 1 has alpha = false isAlphaPre = false ByteInterleavedRaster: width = 256 height = 192 #numDataElements 1 dataOff[0] = 0\"]"}
;; <=

;; @@
(def box 
  (bounding-rect (first contours)))
(def item 
  (submat img box))

(u/mat-view item)
;; @@
;; =>
;;; {"type":"html","content":"<img src=\"data:image/png;base64,\" width=\"41\" height=\"38\" alt=\"\" />","value":"#object[java.awt.image.BufferedImage 0xea60ba8 \"BufferedImage@ea60ba8: type = 5 ColorModel: #pixelBits = 24 numComponents = 3 color space = java.awt.color.ICC_ColorSpace@e1249da transparency = 1 has alpha = false isAlphaPre = false ByteInterleavedRaster: width = 41 height = 38 #numDataElements 3 dataOff[0] = 2\"]"}
;; <=

;; @@

(def segmented-item
  (new-mat (rows item) (cols item) CV_8UC3 background-color))
(copy-to item segmented-item (submat mask box) )

(u/mat-view segmented-item)
;; @@
;; =>
;;; {"type":"html","content":"<img src=\"data:image/png;base64,\" width=\"41\" height=\"38\" alt=\"\" />","value":"#object[java.awt.image.BufferedImage 0x16348204 \"BufferedImage@16348204: type = 5 ColorModel: #pixelBits = 24 numComponents = 3 color space = java.awt.color.ICC_ColorSpace@e1249da transparency = 1 has alpha = false isAlphaPre = false ByteInterleavedRaster: width = 41 height = 38 #numDataElements 3 dataOff[0] = 2\"]"}
;; <=

;; @@

(def center
  (new-point (/ (.cols img ) 2 ) (/ (.rows img) 2)))

(def center-box
  (new-rect
    (- (.-x center ) (/ (.-width box) 2))
    (- (.-y center ) (/ (.-height box) 2))
    (.-width box)
    (.-height box)))

(def result (u/mat-from img))
(set-to result (->scalar "#000000"))
;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-unkown'>#object[org.opencv.core.Mat 0x2e48b2d3 &quot;Mat [ 192*256*CV_8UC3, isCont=true, isSubmat=false, nativeObj=0x7fecf585f7a0, dataAddr=0x158eba040 ]&quot;]</span>","value":"#object[org.opencv.core.Mat 0x2e48b2d3 \"Mat [ 192*256*CV_8UC3, isCont=true, isSubmat=false, nativeObj=0x7fecf585f7a0, dataAddr=0x158eba040 ]\"]"}
;; <=

;; @@
(def final (submat result center-box))
(copy-to segmented-item final (new-mat))

(u/mat-view result)
;; @@
;; =>
;;; {"type":"html","content":"<img src=\"data:image/png;base64,\" width=\"256\" height=\"192\" alt=\"\" />","value":"#object[java.awt.image.BufferedImage 0x7a56b5f3 \"BufferedImage@7a56b5f3: type = 5 ColorModel: #pixelBits = 24 numComponents = 3 color space = java.awt.color.ICC_ColorSpace@e1249da transparency = 1 has alpha = false isAlphaPre = false ByteInterleavedRaster: width = 256 height = 192 #numDataElements 3 dataOff[0] = 2\"]"}
;; <=

