;; gorilla-repl.fileformat = 1

;; **
;;; # Move a shape to the center of the scene
;;; 
;; **

;; @@
(ns unwavering-briars
 (:require
  [opencv3.utils :as u]
  [opencv3.core :refer :all]))
;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-nil'>nil</span>","value":"nil"}
;; <=

;; @@

;; @@

;; @@
(def img 
  (-> "resources/morph/cjy6M.jpg" 
      imread 
      (u/resize-by 0.5) 
      (copy-make-border! 1 1 1 1 
		BORDER_CONSTANT 
        (opencv3.colors.html/->scalar "#aabbcc"))))

(u/mat-view img)
;; @@
;; =>
;;; {"type":"html","content":"<img src=\"data:image/png;base64,\" width=\"320\" height=\"240\" alt=\"\" />","value":"#object[java.awt.image.BufferedImage 0x559bcaef \"BufferedImage@559bcaef: type = 5 ColorModel: #pixelBits = 24 numComponents = 3 color space = java.awt.color.ICC_ColorSpace@e1249da transparency = 1 has alpha = false isAlphaPre = false ByteInterleavedRaster: width = 320 height = 240 #numDataElements 3 dataOff[0] = 2\"]"}
;; <=

;; @@

(def hsv 
  (-> img clone (cvt-color! COLOR_BGR2HSV)))
(u/mat-view hsv)
;; @@
;; =>
;;; {"type":"html","content":"<img src=\"data:image/png;base64,\" width=\"320\" height=\"240\" alt=\"\" />","value":"#object[java.awt.image.BufferedImage 0x4c6f8b48 \"BufferedImage@4c6f8b48: type = 5 ColorModel: #pixelBits = 24 numComponents = 3 color space = java.awt.color.ICC_ColorSpace@e1249da transparency = 1 has alpha = false isAlphaPre = false ByteInterleavedRaster: width = 320 height = 240 #numDataElements 3 dataOff[0] = 2\"]"}
;; <=

;; **
;;; ## thresh hold on yellow in hsv space
;; **

;; @@
(def mask-on-yellow (new-mat))
(in-range hsv (new-scalar 20 100 100) (new-scalar 40 255 255) mask-on-yellow)

(u/mat-view mask-on-yellow)
;; @@
;; =>
;;; {"type":"html","content":"<img src=\"data:image/png;base64,\" width=\"320\" height=\"240\" alt=\"\" />","value":"#object[java.awt.image.BufferedImage 0x4b63092a \"BufferedImage@4b63092a: type = 10 ColorModel: #pixelBits = 8 numComponents = 1 color space = java.awt.color.ICC_ColorSpace@553b8b78 transparency = 1 has alpha = false isAlphaPre = false ByteInterleavedRaster: width = 320 height = 240 #numDataElements 1 dataOff[0] = 0\"]"}
;; <=

;; **
;;; ##find contours, and select first 1, (actually only one)
;; **

;; @@
(def contours (new-arraylist))
(find-contours mask-on-yellow contours (new-mat) RETR_EXTERNAL CHAIN_APPROX_SIMPLE)
(count contours)
;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-unkown'>1</span>","value":"1"}
;; <=

;; @@

(def background-color 
  (new-scalar 0 0 0))

; mask type CV_8UC1 is important !!
(def mask (new-mat (.rows img) (.cols img) CV_8UC1 background-color))

(draw-contours mask contours 0 (new-scalar 255.0) FILLED)
;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-nil'>nil</span>","value":"nil"}
;; <=

;; @@
(def box 
  (bounding-rect (first contours)))
(def item 
  (.submat img box))

(u/mat-view item)
;; @@
;; =>
;;; {"type":"html","content":"<img src=\"data:image/png;base64,\" width=\"52\" height=\"47\" alt=\"\" />","value":"#object[java.awt.image.BufferedImage 0x6b065b03 \"BufferedImage@6b065b03: type = 5 ColorModel: #pixelBits = 24 numComponents = 3 color space = java.awt.color.ICC_ColorSpace@e1249da transparency = 1 has alpha = false isAlphaPre = false ByteInterleavedRaster: width = 52 height = 47 #numDataElements 3 dataOff[0] = 2\"]"}
;; <=

;; @@

(def segmented-item
  (new-mat (.rows item) (.cols item) CV_8UC3 background-color))
(copy-to item segmented-item (.submat mask box) )

(u/mat-view segmented-item)
;; @@
;; =>
;;; {"type":"html","content":"<img src=\"data:image/png;base64,\" width=\"52\" height=\"47\" alt=\"\" />","value":"#object[java.awt.image.BufferedImage 0x3b328a58 \"BufferedImage@3b328a58: type = 5 ColorModel: #pixelBits = 24 numComponents = 3 color space = java.awt.color.ICC_ColorSpace@e1249da transparency = 1 has alpha = false isAlphaPre = false ByteInterleavedRaster: width = 52 height = 47 #numDataElements 3 dataOff[0] = 2\"]"}
;; <=

;; @@

(def center
  (new-point (/ (.cols img ) 2 ) (/ (.rows img) 2)))

(def center-box
  (new-rect
    (- (.-x center ) (/ (.-width box) 2))
    (- (.-y center ) (/ (.-height box) 2))
    (.-width box)
    (.-height box)))

(def result  (u/mat-from img))
(u/mat-view result)

;; @@
;; =>
;;; {"type":"html","content":"<img src=\"data:image/png;base64,\" width=\"320\" height=\"240\" alt=\"\" />","value":"#object[java.awt.image.BufferedImage 0x15dd2300 \"BufferedImage@15dd2300: type = 5 ColorModel: #pixelBits = 24 numComponents = 3 color space = java.awt.color.ICC_ColorSpace@e1249da transparency = 1 has alpha = false isAlphaPre = false ByteInterleavedRaster: width = 320 height = 240 #numDataElements 3 dataOff[0] = 2\"]"}
;; <=

;; @@
(def final (.submat result center-box))
(copy-to segmented-item final (new-mat))

(u/mat-view result)

;; @@
;; =>
;;; {"type":"html","content":"<img src=\"data:image/png;base64,\" width=\"320\" height=\"240\" alt=\"\" />","value":"#object[java.awt.image.BufferedImage 0x543a925a \"BufferedImage@543a925a: type = 5 ColorModel: #pixelBits = 24 numComponents = 3 color space = java.awt.color.ICC_ColorSpace@e1249da transparency = 1 has alpha = false isAlphaPre = false ByteInterleavedRaster: width = 320 height = 240 #numDataElements 3 dataOff[0] = 2\"]"}
;; <=
